const scrollReducer = (state = 1, action) => {
    switch (action.type) {
        case 'SAVE_SCROLL': console.log(action.scrollPos)
            state.scrollLastSize = action.scrollPos
            return state.scrollLastSize
        default:
            return state.scrollLastSize
    }
}

export default scrollReducer
